import { useState, useEffect } from 'react'
import { accessToken, logout, getCurrentUserProfile } from './spotify'
import styled from 'styled-components/macro';
import { GlobalStyle } from './styles'
import { catchErrors } from './utils'
import {BrowserRouter as Router,
  Routes,
  useLocation,
  Route
} from "react-router-dom";
import { Start, Form, Login, Home } from './pages';



const StyledLoginButton = styled.a`
  background-color: var(--green);
  color: var(--white);
  padding: 10px 20px;
  margin: 20px;
  border-radius: 30px;
  display: inline-block;
`;




// Scroll to top of page when changing routes
// https://reactrouter.com/web/guides/scroll-restoration/scroll-to-top
function ScrollToTop() { 
  const { pathname } = useLocation();

  useEffect(() => {
    window.scrollTo(0,0);
  }, [pathname]);
}

function App() {
  const [token, setToken] = useState(null)
  const [profile, setProfile] = useState(null)

  useEffect(() => {
    setToken(accessToken);

    const fetchData = async () => {      
      const { data } = await getCurrentUserProfile();
      setProfile(data);
    }

    catchErrors(fetchData())
  }, []);

  return (
    <div className="App">
      <GlobalStyle />
      <header className="App-header">
        <Home/>

        <Router>
          <Routes>
            <Route path="/start" element={ <Start/> }></Route>

            <Route path="/forms" element={ <Form/> }></Route>                          

            <Route path="/top-artists" element={<h1>Top Artists</h1>}></Route>

            <Route path="/top-tracks" element={<h1>Top Tracks</h1>}></Route>

            <Route path="/playlists/:id" element={<h1>Playlists</h1>}></Route>

            <Route path="/playlists/" element={<h1>Playlists</h1>}></Route>

            <Route path="/" element={ <Home/> }></Route>

          </Routes>
        </Router>


      </header>
    </div>
  );
}

export default App;
